use std::env;

// Default parameters for the circuit, used for testing
const DEFAULT_PARAMS_STR: &str = "";

/*
   Get the parameters for the circuit
*/
pub fn get_params() -> Result<Vec<u8>, hex::FromHexError> {
    match env::var("ENVIRONMENT") {
        Ok(val) if val == "production" => {
            println!("We are in production! Fetch params from DynamoDB");
            //TODO: Fetch from DynamoDB instead of decoding a static string
            // return get_params_from_dynamodb();
        }
        _ => {
            println!("We are in development or an invalid environment, using default params.");
        }
    }
    hex::decode(DEFAULT_PARAMS_STR)
}
